import React, { useState, useEffect } from "react";
import Countdown from "./components/Countdown";
import SpeakerSchedule from "./components/SpeakerSchedule";
import EventStats from "./components/EventStats";
import LoadingScreen from "./components/LoadingScreen";
import "./styles/App.css";

const App = () => {
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState("dashboard");
  const [showRegister, setShowRegister] = useState(false);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [registered, setRegistered] = useState(false);

  useEffect(() => {
    setLoading(false);
  }, []);

  const handleRegister = () => {
    if (name.trim() && email.trim()) {
      setRegistered(true);
      setTimeout(() => setRegistered(false), 3000); // Celebration animation duration
    }
  };

  return (
    <div className="app-container">
      {loading ? (
        <LoadingScreen />
      ) : (
        <>
          <h1 className="dashboard-title">üöÄ GDG TechSphere 2025: The Future Unleashed</h1>

          <div className="button-container">
            <button className="toggle-btn red-bg" onClick={() => setView(view === "dashboard" ? "speakers" : "dashboard")}>
              {view === "dashboard" ? "üé§ View Speaker Schedule" : "üè† View Dashboard"}
            </button>

            <div className="event-start-box blue-bg">
              <p>Event Starts In</p>
              <Countdown />
            </div>

            <button className="register-btn green-bg" onClick={() => setShowRegister(!showRegister)}>
              üìù Register
            </button>
          </div>

          {showRegister && (
            <div className="register-box">
              <input type="text" placeholder="Enter Name" value={name} onChange={(e) => setName(e.target.value)} />
              <input type="email" placeholder="Enter Email" value={email} onChange={(e) => setEmail(e.target.value)} />
              <button className="submit-btn" onClick={handleRegister}>Submit</button>
            </div>
          )}

          {registered && <div className="celebration-animation">üéâ Registration Successful! üéä</div>}

          <div className="view-container">
            {view === "dashboard" ? <EventStats /> : <SpeakerSchedule />}
          </div>
        </>
      )}
    </div>
  );
};

export default App;
