import React, { useState, useEffect } from "react";
import Countdown from "./components/Countdown";
import CelebrateButton from "./components/CelebrateButton";
import SpeakerSchedule from "./components/SpeakerSchedule";
import EventStats from "./components/EventStats";
import LoadingScreen from "./components/LoadingScreen";
import "./styles/App.css";

const App = () => {
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState("dashboard");

  useEffect(() => {
    setTimeout(() => setLoading(false), 2000);
  }, []);

  return (
    <div className="app-container">
      {loading ? (
        <LoadingScreen />
      ) : (
        <>
          <button
            className={`toggle-btn ${view === "dashboard" ? "active" : ""}`}
            onClick={() => setView(view === "dashboard" ? "speakers" : "dashboard")}
          >
            {view === "dashboard" ? "üìÖ View Speakers" : "üè† Back to Dashboard"}
          </button>

          {view === "dashboard" ? (
            <div className="dashboard">
              <h1>üöÄ Welcome to GDG Interactive Dashboard</h1>
              <Countdown />
              <CelebrateButton />
              <EventStats />
            </div>
          ) : (
            <SpeakerSchedule />
          )}
        </>
      )}
    </div>
  );
};

export default App;
