import React, { useState, useEffect } from "react";
import Countdown from "./components/Countdown";
import CelebrateButton from "./components/CelebrateButton";
import SpeakerSchedule from "./components/SpeakerSchedule";
import EventStats from "./components/EventStats";
import LoadingScreen from "./components/LoadingScreen";
import "./styles/App.css";

const App = () => {
  const [view, setView] = useState("dashboard");

  return (
    <div className="app-container">
      <h1 className="dashboard-title">üöÄ GDG TechSphere 2025: The Future Unleashed</h1>

      {/* üî• Button Container for Side-by-Side Layout */}
      <div className="button-container">
        <button className="toggle-btn" onClick={() => setView(view === "dashboard" ? "speakers" : "dashboard")}>
          {view === "dashboard" ? "üé§ View Speaker Schedule" : "üè† View Dashboard"}
        </button>

        <button className="celebrate-btn">üéâ Celebrate</button>

        <button className="register-btn">üèÜ Register</button>
      </div>

      <div className="view-container">
        {view === "dashboard" ? (
          <div className="dashboard">
            <Countdown />
            <EventStats />
          </div>
        ) : (
          <SpeakerSchedule />
        )}
      </div>
    </div>
  );
};

export default App;
