import React, { useState, useEffect } from "react";
import Countdown from "./components/Countdown";
import SpeakerSchedule from "./components/SpeakerSchedule";
import EventStats from "./components/EventStats";
import LoadingScreen from "./components/LoadingScreen";
import CelebrationEffect from "./components/CelebrationEffect"; // üéâ Added for animation
import "./styles/App.css";

const App = () => {
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState("dashboard");
  const [showRegister, setShowRegister] = useState(false);
  const [submitted, setSubmitted] = useState(false); // ‚úÖ Celebration trigger

  useEffect(() => {
    setLoading(false);
  }, []);

  const handleSubmit = () => {
    setSubmitted(true);
    setTimeout(() => setSubmitted(false), 4000); // üéâ Celebration disappears after 4s
  };

  return (
    <div className="app-container">
      {loading ? (
        <LoadingScreen />
      ) : (
        <>
          <h1 className="dashboard-title">üöÄ GDG TechSphere 2025: The Future Unleashed</h1>

          {/* üéØ Event Countdown Box (Small, Left Corner) */}
          <div className="event-countdown-box">
            <Countdown />
            <p className="event-text">Event Starts In</p>
          </div>

          {/* üéØ Buttons Section (View Speakers & Register Side-by-Side) */}
          <div className="buttons-container">
            <button className="view-speakers-btn" onClick={() => setView(view === "dashboard" ? "speakers" : "dashboard")}>
              {view === "dashboard" ? "üé§ View Speaker Schedule" : "üè† View Dashboard"}
            </button>
            <button className="register-btn" onClick={() => setShowRegister(!showRegister)}>‚úç Register</button>
          </div>

          {/* üéØ Show Input Fields When Register Clicked */}
          {showRegister && (
            <div className="registration-form">
              <input type="text" placeholder="Full Name" className="input-field" />
              <input type="email" placeholder="Email" className="input-field" />
              <input type="tel" placeholder="Phone Number" className="input-field" />
              <button className="submit-btn" onClick={handleSubmit}>Submit</button>
            </div>
          )}

          {/* üéâ Celebration Animation When Submitted */}
          {submitted && <CelebrationEffect />}

          {/* üéØ Speaker Schedule & Event Stats */}
          <div className="view-container">
            {view === "dashboard" ? <EventStats /> : <SpeakerSchedule />}
          </div>
        </>
      )}
    </div>
  );
};

export default App;
