import React, { useState, useEffect } from "react";
import Countdown from "./components/Countdown";
import SpeakerSchedule from "./components/SpeakerSchedule";
import EventStats from "./components/EventStats";
import LoadingScreen from "./components/LoadingScreen";
import "./styles/App.css";
import gdgLogo from "./assets/gdg.logo.png";


const App = () => {
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState("dashboard");
  const [showRegister, setShowRegister] = useState(false);

  useEffect(() => {
    setLoading(false);
  }, []);

  return (
    <div className="app-container">
      {loading ? (
        <LoadingScreen />
      ) : (
        <>
          
          {/* üéØ Event Countdown (Left Middle) */}
          <div className="event-countdown-box">
            <Countdown />
          </div>
          {/* üöÄ GDG TechSphere 2025 Title & Logo */}
          <div className="title-container">
            <h1 className="dashboard-title">
              üöÄ GDG <span className="highlight-text">TechSphere 2025</span>: The Future Unleashed
            </h1>
            <img src={gdgLogo} alt="GDG Logo" className="gdg-logo" />
          </div>

          {/* üéØ Event Countdown (Left Middle) */}
          <div className="event-countdown-box">
            <Countdown />
          </div>

          {/* üéØ Buttons Section */}
          <div className="buttons-container">
            <button
              className="view-speakers-btn"
              onClick={() => setView(view === "dashboard" ? "speakers" : "dashboard")}
            >
              {view === "dashboard" ? "üé§ View Speaker Schedule" : "üè† View Dashboard"}
            </button>

            <button className="register-btn" onClick={() => setShowRegister(!showRegister)}>
              ‚úç Register
            </button>
          </div>

          {/* üéØ Registration Form */}
          {showRegister && (
            <div className="registration-form">
              <input type="text" placeholder="Full Name" className="input-field" />
              <input type="email" placeholder="Email" className="input-field" />
              <input type="tel" placeholder="Phone Number" className="input-field" />
              <button className="submit-btn">Submit</button>
            </div>
          )}

          {/* üìÖ Speaker Schedule & Event Stats */}
          <div className="view-container">
            {view === "dashboard" ? <EventStats /> : <SpeakerSchedule />}
          </div>
        </>
      )}
    </div>
  );
};

export default App;
