import React from "react";
import { motion } from "framer-motion";
import EventStats from "./EventStats";
import SpeakerSchedule from "./SpeakerSchedule";
import Confetti from "react-confetti";
import { useState, useEffect } from "react";
import "../styles/style.css";

const Dashboard = () => {
  const [showConfetti, setShowConfetti] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowConfetti(false);
    }, 7000); // Confetti lasts for 7 seconds

    return () => clearTimeout(timer);
  }, []);

  return (
    <motion.div 
      className="dashboard"
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.8, ease: "easeOut" }}
    >
      {showConfetti && <Confetti />}
      <h1>ðŸš€ GDG Interactive Dashboard</h1>
      <EventStats />
      <SpeakerSchedule />
    </motion.div>
  );
};
<motion.img 
  src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/29/GDG_Brand.svg/2560px-GDG_Brand.svg.png" 
  alt="GDG Logo" 
  className="gdg-logo"
  whileHover={{ rotate: 10, scale: 1.1 }}
/>

export default Dashboard;
