import React, { useState, useEffect } from "react";
import Confetti from "react-confetti";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";
import { eventStats } from "../data"; // Import event statistics data
import "../styles/dashboard.css"; // Import styles

function Dashboard() {
  const [showConfetti, setShowConfetti] = useState(false);
  const [timeLeft, setTimeLeft] = useState(0);
  const [windowSize, setWindowSize] = useState({ width: window.innerWidth, height: window.innerHeight });

  // Update window size for Confetti effect
  useEffect(() => {
    const handleResize = () => {
      setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Event countdown timer
  useEffect(() => {
    const eventDate = new Date("2025-03-22T23:59:59").getTime();
    const interval = setInterval(() => {
      const now = new Date().getTime();
      const difference = eventDate - now;
      setTimeLeft(difference > 0 ? difference : 0);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // Convert milliseconds to days, hours, minutes, seconds
  const formatTime = (ms) => {
    const days = Math.floor(ms / (1000 * 60 * 60 * 24));
    const hours = Math.floor((ms % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((ms % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((ms % (1000 * 60)) / 1000);
    return `${days}d ${hours}h ${minutes}m ${seconds}s`;
  };

  const startCelebration = () => {
    setShowConfetti(true);
    setTimeout(() => {
      setShowConfetti(false);
    }, 10000); // Confetti lasts for 10 seconds
  };

  return (
    <div className="dashboard-container fade-in">
      {showConfetti && <Confetti width={windowSize.width} height={windowSize.height} />}
      <h1 className="dashboard-title">üöÄ Welcome to GDG Interactive Dashboard</h1>

      {/* Countdown Timer */}
      <div className="countdown-box">
        <h2>‚è≥ Event Countdown</h2>
        <p className="countdown-timer">{formatTime(timeLeft)}</p>
      </div>

      {/* Celebration Button */}
      <button className="celebrate-btn" onClick={startCelebration}>
        üéâ Celebrate
      </button>

      {/* Event Stats Graph */}
      <div className="stats-container">
        <h2>üìä Event Attendance Stats</h2>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={eventStats}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="day" />
            <YAxis />
            <Tooltip />
            <Line type="monotone" dataKey="attendees" stroke="#007bff" strokeWidth={3} />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

export default Dashboard;
